import React from 'react'

const RecipeListItem = ({ recipe, onRecipeSelect }) => {
    const { title, thumbnail, ingredients, removeIcon, editIcon } = recipe

    function myfunction(recipe) {
        console.log("CLICKED", recipe);
        // addToMyList = !addToMyList
    }

    return (
        <ul>
            {/* <div className="media-left"> */}
            {/* {addToMyList === true ? <img src="../assets/images/square_check.png" alt="my image" onClick={() => myfunction(recipe)}
                    style={{ width: 50, height: 50, left: 10 }}
                /> : <img src="../assets/images/square_non_check.png" alt="my image" onClick={() => myfunction(recipe)}
                    style={{ width: 50, height: 50, left: 10 }}
                    />} */}
            {/* </div> */}
            <li onClick={() => onRecipeSelect(recipe)} className="list-group-item">
                <div className="recipe-list-media">

                    <div className="media-left">
                        <img className="media-object" src={thumbnail || '../assets/images/no-image-available.jpg'} />
                    </div>
                    <div className="media-body">
                        <div className="media-heading title">{title}</div>
                        <p>Ingredients: <span className="ingredients">{ingredients}</span></p>
                    </div>
                </div>
            </li>
            <div className="media-right">
                {editIcon === true ? <img src="../assets/images/edit.png" alt="edit image" onClick={() => myfunction(recipe)}
                    style={{ width: 20, height: 20, marginRight: 8 }}
                /> : null}
                {removeIcon === true ? <img src="../assets/images/close.png" alt="delete image" onClick={() => myfunction(recipe)}
                    style={{ width: 20, height: 20, marginRight: 8 }}
                /> : null}
            </div>
        </ul>
    )
}

export default RecipeListItem
