import { SHOW_RECIPE, FETCH_RECIPES, FETCH_STORAGE_RECIPES, SAVE_TO_STORAGE } from '../actions/index'
const initialState = {
    fetchRecipes: [],
    fetchStorageRecipes: []
}
const MAX_RESULTS = 20

export default function RecipeReducer(state = initialState, action) {
    switch (action.type) {
        case FETCH_RECIPES:
            return {
                ...state,
                fetchRecipes: action.payload.data.results.slice(0, MAX_RESULTS)
            }
        case FETCH_STORAGE_RECIPES:
            return {
                ...state,
                fetchStorageRecipes: action.payload
            }
        case SAVE_TO_STORAGE:
            console.log('進來reducer 已存好')
            return state
        case SHOW_RECIPE:
            const win = window.open(action.payload, '_blank')
            win.focus()
            return state
        default:
            return state
    }
}
