    // }

    // fetchData() {
    //   const REQUEST_URL = 'http://www.recipepuppy.com/'

    //   fetch(REQUEST_URL)
    //     .then((response) => response.json())
    //     .then((responseData) => {
    //       let getAll = []

    //       responseData.map(a => {
    //         if (a.addToMyList == true) {
    //           getAll.push(a)
    //         }
    //       })

    //       this.setState({ dataSource: getAll })


    //     })
    //     .catch((err) => {
    //       console.log('error 是 ', err)
    //     })
    // }

    const getRecipeItems = () => {
        // console.log('要來列出list了！！！', { recipes })
        if (recipes.length > 0) {

            // console.log('要開始渲染資料！', recipes)
            return (recipes.map(recipe => {
                // console.log('每一筆資料：：：', recipe)
                return (
                    <RecipeListItem
                        recipe={recipe}
                        key={recipe.title}
                        // onRecipeSelect={recipe => disPatch(showRecipe(recipe.href))}
                        onRecipeSelect={recipe => {
                            if (recipe.href.length > 0) {
                                disPatch(showRecipe(recipe.href))
                            } else {
                                message.error(`No link here`, 2.5);
                            }
                        }
                        }
                    />
                )
            }))
        }
    }


    return (
        <ul className="list-group">
            {getRecipeItems()}
        </ul>
    )

}
