import { SHOW_RECIPE, FETCH_RECIPES } from '../actions/index'
const MAX_RESULTS = 20

const initialState = {
    recipeAll: []
}

export default function (state = initialState, action) {
    switch (action.type) {
        case FETCH_RECIPES:
            console.log("initialState??::::", initialState)
            console.log("action.payload??:::", action.payload)
            // return action.payload.data.results.slice(0, MAX_RESULTS)
            return action.payload.recipeAll.slice(0, MAX_RESULTS)
        case SHOW_RECIPE:
            const win = window.open(action.payload.recipeAll, '_blank')
            win.focus()
            return state
    }
    return state
}
