import React, { useState, useEffect } from 'react'
// import { connect } from 'react-redux'
import { useDispatch } from 'redux-react-hook'
// import { bindActionCreators } from 'redux'
// import { bindActionCreators, dispatch } from 'redux'
import _ from 'lodash'
import { fetchRecipes, showRecipe } from '../actions'
import RecipeListItem from './recipe_list_item'
import 'antd/dist/antd.css';
import { message } from 'antd';


export default function SearchBar(props) {
    const disPatch = useDispatch()
    const [term, setTerm] = useState('')
    const [myLists, setMyLists] = useState([])
    const [searchLists, setSearchLists] = useState([])

    useEffect(() => {
        disPatch(fetchRecipes(''))
        loadStorage()
        // TODO: 會偵測到這頁嗎？
    }, [])

    const loadStorage = () => {
        let loadDatas = JSON.parse(localStorage.getItem('myDatas'))
        if (loadDatas) {
            setMyLists(loadDatas)
        }
    }

    const searchMyLists = (term) => {
        const re = new RegExp(`${term}`);
        const sLists = myLists.filter(record => (record.title.match(re) || record.ingredients.match(re)))
        setSearchLists(sLists)
    }

    const onInputChange = (term) => {
        const debouncedFetchRecipes = _.debounce(term => {
            disPatch(fetchRecipes(term))
            searchMyLists(term)
        }, 300)
        // const debouncedFetchRecipes = _.debounce(term => { bindActionCreators(fetchRecipes(term), disPatch) }, 300)
        // console.log('有道search bar 這裡了媽？', term)
        setTerm(term)
        debouncedFetchRecipes(term)
    }

    const getMyRecipeItems = () => {

        if (myLists.length > 0) {

            let renderLists = []
            if (term !== '') {
                renderLists = searchLists
            } else {
                renderLists = myLists
            }

            return (renderLists.map(myList => {
                // console.log('每一筆資料 my：：：', myList)
                return (
                    <RecipeListItem
                        recipe={myList}
                        key={myList.id}
                        onRecipeSelect={myList => {
                            if (myList.href.length > 0) {
                                disPatch(showRecipe(myList.href))
                            } else {
                                message.error(`No link here`, 2.5);
                            }
                        }
                        }
                    />
                )
            }))
        }
    }

    return (
        <div className="search-bar">
            <input
                autoFocus
                value={term}
                onChange={e => onInputChange(e.target.value)}
                placeholder="Search By Ingredients..."
            />
            <ul className="list-group">
                {getMyRecipeItems()}
            </ul>
        </div>
    )



}

