import { SHOW_RECIPE, FETCH_RECIPES } from '../actions/index'
const MAX_RESULTS = 20

// const initialState = {
//     recipeAll: []
// }

export default function RecipeReducer(state = [], action) {
    console.log('有到reducer媽？？？？', action, action.type)
    switch (action.type) {
        case FETCH_RECIPES:
            // console.log('action.payload???:::', action.payload)
            // console.log('action.payload.data???:::', action.payload.data)
            return action.payload.data.results.slice(0, MAX_RESULTS)
        case SHOW_RECIPE:
            const win = window.open(action.payload, '_blank')
            win.focus()
            return state
        default:
            { console.log('來switch!', action.type) }
            return state
    }
    // return state
}
