import axios from 'axios'

export const FETCH_RECIPES = 'fetch_recipes'
export const SHOW_RECIPE = 'show_recipe'

export function fetchRecipes(term) {

    return async (dispatch) => {
        // const url = `/recipe-puppy-api/?q=${term}`
        // const url = `http://www.recipepuppy.com/api/?q=${term}`
        const url = `/api/?q=${term}`

        const request = await axios.get(url)
        console.log('url???:::', url)
        console.log('有拿到值嗎？？？？', request)

        return request.then(
            response => dispatch({
                type: FETCH_RECIPES,
                // payload: request,
                payload: response,
            })
        )

        // return {
        //     type: FETCH_RECIPES,
        //     payload: request,
        //     // payload: response,
        // }

    }
}

export function showRecipe(url) {
    return {
        type: SHOW_RECIPE,
        payload: url,
    }
}
