import React, { useState } from 'react'
import 'antd/dist/antd.css';
import { Form, Input, Button, Radio } from 'antd';
// import { useDispatch } from 'redux-react-hook'
import _ from 'lodash'

export default function FormList(props) {
    // const disPatch = useDispatch()

    // const [term, setTerm] = useState('')

    // const onInputChange = (term) => {
    //     const debouncedFetchRecipes = _.debounce(term => { disPatch(fetchRecipes(term)) }, 300)
    //     setTerm(term)
    //     debouncedFetchRecipes(term)
    // }
    const formRef = React.createRef();
    // const [form] = Form.useForm();
    // const formLayout = 'vertical'
    const layout = {
        labelCol: {
            span: 8,
        },
        wrapperCol: {
            span: 16,
        },
    };
    const tailLayout = {
        wrapperCol: {
            offset: 8,
            span: 16,
        },
    };

    const onFinish = (values) => {
        console.log(values);
    };
    const onReset = () => {
        formRef.current.resetFields();
    };

    return (
        // <div className="search-bar">
        //     <input
        //         autoFocus
        //         value={term}
        //         onChange={e => onInputChange(e.target.value)}
        //         placeholder="Search By Ingredients..."
        //     />
        // </div>
        <Form
            // {...formItemLayout}
            // layout={formLayout}
            // form={form}
            {...layout}
            ref={formRef} name="control-ref" onFinish={() => onFinish()}
        // onValuesChange={onFormLayoutChange}
        >

            <Form.Item name="title" label="Recipe Title" rules={[{ required: true }]}>
                <Input placeholder="e.g spaghetti, sushi, fries" />
            </Form.Item>
            <Form.Item name="ingredients" label="Ingredients" rules={[{ required: true }]}>
                <Input placeholder="e.g onion, garlic, bread, etc" />
            </Form.Item>
            <Form.Item name="link" label="Recipe Link" rules={[{ required: false }]}>
                <Input placeholder="recipe link..." />
            </Form.Item>
            <Form.Item name="image link" label="Image" rules={[{ required: false }]}>
                <Input placeholder="recipe image..." />
            </Form.Item>
            <Form.Item {...tailLayout}>
                <Button type="primary" htmlType="submit">Submit</Button>
                <Button htmlType="button" onClick={() => onReset()}>Reset</Button>
            </Form.Item>
        </Form>

    )



}
