import React, { useState } from 'react'
import { connect } from 'react-redux'
// import { useDispatch } from 'redux-react-hook'
// import { bindActionCreators } from 'redux'
import { bindActionCreators, dispatch } from 'redux'
import _ from 'lodash'
import { fetchRecipes } from '../actions'


function SearchBar(props) {
    const disPatch = useDispatch()

    // state = { term: '' }
    const [term, setTerm] = useState('')

    const onInputChange = (term) => {
        // const debouncedFetchRecipes = _.debounce(term => { disPatch(fetchRecipes(term)) }, 300)
        // const debouncedFetchRecipes = _.debounce(term => { bindActionCreators(fetchRecipes(term), disPatch) }, 300)
        const debouncedFetchRecipes = _.debounce(term => { this.props.fetchRecipes(term) }, 300)
        console.log('有道search bar 這裡了媽？', term)
        setTerm(term)
        debouncedFetchRecipes(term)
    }

    return (
        <div className="search-bar">
            <input
                autoFocus
                value={term}
                onChange={e => onInputChange(e.target.value)}
                placeholder="Search By Ingredients..."
            />
        </div>
    )



}

function mapDispatchToProps(dispatch) {
    return bindActionCreators({ fetchRecipes }, dispatch)
}

export default connect(null, mapDispatchToProps)(SearchBar)
