import { changeName } from './actions'
// import logo from './logo.svg';
import React, { useState } from 'react'
import './App.css';
import 'antd/dist/antd.css';
import { Tabs } from 'antd';
// import { Provider } from 'react-redux';
// import ReduxPromise from 'redux-promise'
// import { createStore, applyMiddleware } from 'redux';
// import reducers from './reducers';
import SearchBar from './components/search_bar'
import RecipeList from './components/recipe_list'
import FormList from './components/form_list'
// const createStoreWithMiddleware = applyMiddleware(ReduxPromise)(createStore)

//for redux
import configureStore from './store'
import { StoreContext, useDispatch } from 'redux-react-hook'
const store = configureStore()


const { TabPane } = Tabs;

function App() {
  const dispatch = useDispatch()
  const [activeTab, setActiveTab] = useState('1')

  const changeTab = (activeKey) => {
    console.log(activeKey);
    setActiveTab(activeKey)
    dispatch(changeName(activeKey))
  };
  return (
    <div className="card-container">
      <Tabs type="card" activeKey={activeTab} onChange={changeTab}>
        <TabPane tab="Search Recipes" key="1">
          <div className="main-container">
            <p>Awesome Recipes</p>
            <SearchBar />
            <RecipeList />
          </div>
          {/* <p>Content of Tab Pane 1</p>
          <p>Content of Tab Pane 1</p>
          <p>Content of Tab Pane 1</p> */}
        </TabPane>
        <TabPane tab="Add Recipes" key="2">
          <div className="main-container">
            <p>My Recipes</p>
            <FormList />
          </div>
        </TabPane>
        {/* <TabPane tab="Tab Title 3" key="3">
          <p>Content of Tab Pane 3</p>
          <p>Content of Tab Pane 3</p>
          <p>Content of Tab Pane 3</p>
        </TabPane> */}
      </Tabs>
    </div>
    // <div className="App">
    //   <header className="App-header">
    //     <img src={logo} className="App-logo" alt="logo" />
    //     <p>
    //       Edit <code>src/App.js</code> and save to reload.
    //     </p>
    //     <a
    //       className="App-link"
    //       href="https://reactjs.org"
    //       target="_blank"
    //       rel="noopener noreferrer"
    //     >
    //       Learn React
    //     </a>
    //   </header>
    // </div>
  );
}

