import axios from 'axios'

export const FETCH_RECIPES = 'fetch_recipes'
export const SHOW_RECIPE = 'show_recipe'

export function fetchRecipes(term) {
    // const url = `/recipe-puppy-api/?q=${term}`
    const url = `http://www.recipepuppy.com/api/?q=${term}`
    const request = axios.get(url)
    console.log('url???:::', url)
    console.log('有拿到值嗎？？？？', request)

    return {
        type: FETCH_RECIPES,
        payload: request,
        // payload: response,
    }
}

export function showRecipe(url) {
    return {
        type: SHOW_RECIPE,
        payload: url,
    }
}
