import axios from 'axios'

export const FETCH_RECIPES = 'fetch_recipes'
export const SHOW_RECIPE = 'show_recipe'

export async function fetchRecipes(term) {
    const url = `/recipe-puppy-api/?q=${term}`
    let request = axios.get(url)
    // const url = `http://www.recipepuppy.com/api/?q=${term}`;
    console.log('url???:::', url)

    // let request = await fetch(url)
    console.log('有拿到值嗎？？？？', request)

    // let response = await request.json()
    // console.log('response???:::', response)



    return {
        type: FETCH_RECIPES,
        payload: request,
        // payload: response,
    }
}

export function showRecipe(url) {
    return {
        type: SHOW_RECIPE,
        payload: url,
    }
}
